import { Component, OnInit } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { ClientApiService } from '../../services/client-api.service';
import { Client } from '../../models/client.model';

@Component({
  selector: 'app-client-form-page',
  templateUrl: './client-form-page.component.html'
})
export class ClientFormPageComponent implements OnInit {
  mode: 'create' | 'edit' = 'create';
  clientId: string | null = null;

  loading = false;
  saving = false;
  error: string | null = null;

  form = this.fb.group({
    clientRef: ['', [Validators.required, Validators.maxLength(30)]],
    fullName: ['', [Validators.required, Validators.maxLength(120)]],
    email: ['', [Validators.required, Validators.email]],
    phone: ['', [Validators.required, Validators.maxLength(40)]],
    riskCategory: ['Medium' as Client['riskCategory'], [Validators.required]],
    classification: ['Standard', [Validators.required, Validators.maxLength(50)]],
    advisor: ['', [Validators.required, Validators.maxLength(80)]],
    notes: ['']
  });

  constructor(
    private fb: FormBuilder,
    private route: ActivatedRoute,
    private router: Router,
    private api: ClientApiService
  ) {}

  ngOnInit(): void {
    const id = this.route.snapshot.paramMap.get('id');
    if (id) {
      this.mode = 'edit';
      this.clientId = id;
      this.loadClient(id);
    }
  }

  private loadClient(id: string): void {
    this.loading = true;
    this.api.get(id).subscribe({
      next: (c) => {
        this.form.patchValue({
          clientRef: c.clientRef,
          fullName: c.fullName,
          email: c.email,
          phone: c.phone,
          riskCategory: c.riskCategory,
          classification: c.classification,
          advisor: c.advisor,
          notes: c.notes || ''
        });
        // Keep clientRef editable? If you want it immutable, disable here.
        this.loading = false;
      },
      error: (err) => {
        this.error = err?.error?.message ?? 'Failed to load client';
        this.loading = false;
      }
    });
  }

  save(): void {
    if (this.form.invalid) {
      this.form.markAllAsTouched();
      return;
    }

    this.saving = true;
    this.error = null;

    const payload = this.form.getRawValue() as Client;

    if (this.mode === 'create') {
      this.api.create(payload).subscribe({
        next: (created) => {
          this.saving = false;
          this.router.navigate(['/clients', created._id]);
        },
        error: (err) => {
          this.error = err?.error?.message ?? 'Failed to create client';
          this.saving = false;
        }
      });
    } else {
      if (!this.clientId) return;
      this.api.update(this.clientId, payload).subscribe({
        next: (updated) => {
          this.saving = false;
          this.router.navigate(['/clients', updated._id]);
        },
        error: (err) => {
          this.error = err?.error?.message ?? 'Failed to update client';
          this.saving = false;
        }
      });
    }
  }
}
<div class="d-flex align-items-center justify-content-between mb-3">
  <div>
    <h2 class="mb-0">{{ mode === 'create' ? 'New Client' : 'Edit Client' }}</h2>
    <div class="text-muted">Client record management</div>
  </div>
  <a class="btn btn-outline-secondary" routerLink="/clients">Back</a>
</div>

<div *ngIf="error" class="alert alert-danger">{{ error }}</div>
<div *ngIf="loading" class="alert alert-info">Loading...</div>

<form class="card" [formGroup]="form" (ngSubmit)="save()">
  <div class="card-body">
    <div class="row g-3">
      <div class="col-md-4">
        <label class="form-label">Client Ref</label>
        <input class="form-control" formControlName="clientRef" placeholder="NS-0001" />
        <div class="text-danger small" *ngIf="form.controls.clientRef.touched && form.controls.clientRef.invalid">
          Client Ref is required.
        </div>
      </div>

      <div class="col-md-8">
        <label class="form-label">Full Name</label>
        <input class="form-control" formControlName="fullName" placeholder="Jane Doe" />
        <div class="text-danger small" *ngIf="form.controls.fullName.touched && form.controls.fullName.invalid">
          Full Name is required.
        </div>
      </div>

      <div class="col-md-6">
        <label class="form-label">Email</label>
        <input class="form-control" formControlName="email" placeholder="jane.doe@northstar.com" />
        <div class="text-danger small" *ngIf="form.controls.email.touched && form.controls.email.invalid">
          Provide a valid email.
        </div>
      </div>

      <div class="col-md-6">
        <label class="form-label">Phone</label>
        <input class="form-control" formControlName="phone" placeholder="(416) 555-0100" />
        <div class="text-danger small" *ngIf="form.controls.phone.touched && form.controls.phone.invalid">
          Phone is required.
        </div>
      </div>

      <div class="col-md-4">
        <label class="form-label">Risk Category</label>
        <select class="form-select" formControlName="riskCategory">
          <option value="Low">Low</option>
          <option value="Medium">Medium</option>
          <option value="High">High</option>
        </select>
      </div>

      <div class="col-md-4">
        <label class="form-label">Classification</label>
        <input class="form-control" formControlName="classification" placeholder="Standard" />
      </div>

      <div class="col-md-4">
        <label class="form-label">Advisor</label>
        <input class="form-control" formControlName="advisor" placeholder="Advisor Name" />
      </div>

      <div class="col-12">
        <label class="form-label">Notes</label>
        <textarea class="form-control" rows="4" formControlName="notes" placeholder="Operational notes for advisor/compliance"></textarea>
      </div>
    </div>
  </div>

  <div class="card-footer d-flex justify-content-end gap-2">
    <button class="btn btn-primary" type="submit" [disabled]="saving">
      {{ saving ? 'Saving...' : (mode === 'create' ? 'Create' : 'Update') }}
    </button>
  </div>
</form>
